jQuery(document).ready(function($){$("#post-submission-form").on("submit",function(t){t.preventDefault();var e=$("#post-submission-title").val(),o=$("#post-submission-excerpt").val(),n=$("#post-submission-content").val(),s="publish",c={title:e,excerpt:o,content:n,status:"publish"};$.ajax({method:"POST",url:POST_SUBMITTER.root+"wp/v2/posts",data:c,beforeSend:function(t){t.setRequestHeader("X-WP-Nonce",POST_SUBMITTER.nonce)},success:function(t){console.log(t),alert(POST_SUBMITTER.success)},fail:function(t){console.log(t),alert(POST_SUBMITTER.failure)}})})}),jQuery(document).ready(function($){$("#post-comment-form").on("submit",function(t){t.preventDefault();var e=$("#post-comment-content").val(),o=$("#post-comment-id").val(),n="publish",s={content:e,post:o,status:"publish"};$.ajax({method:"POST",url:COMMENT_SUBMITTER.root+"wp/v2/comments",data:s,beforeSend:function(t){t.setRequestHeader("X-WP-Nonce",COMMENT_SUBMITTER.nonce)},success:function(t){console.log(t),alert(COMMENT_SUBMITTER.success)},fail:function(t){console.log(t),alert(COMMENT_SUBMITTER.failure)}})})});